mapper.insert:


	SELECT LAST_INSERT_ID() as boardNo

INSERT INTO board(
	  work_no
	, board_title
	, board_content


		, board_location
		, board_detail_location


	, board_auth
	, board_fix
	, board_writer
	, create_date
	, update_date
) VALUES (
	  #{workNo} 
	, #{boardTitle}
	, #{boardContent}


		, #{boardLocation}
		, #{boardDetailLocation}

	, #{boardAuth}
	, 'N'
	, #{boardWriter}
	, now()
	, now()
)

UPDATE
	board
SET
	board_fix = #{boardFix}
WHERE
	board_no = #{boardNo}

mapper.update:
UPDATE
	board
SET
	board_title = #{boardTitle}
	, board_content = #{boardContent}

		, board_location = #{boardLocation}
		, board_detail_location = #{boardDetailLocation}

	, board_auth = #{boardAuth}
	, update_date = NOW()
WHERE
	board_no = #{boardNo}
