mapper.insert:
INSERT INTO project_member(
	work_member_no
	, project_no
	, project_member_auth
	, active
	, create_date
	, update_date
) VALUES (
	#{workMemberNo}
	, #{projectNo}
	, #{projectMemberAuth}
	, 'Y'
	, NOW()
	, NOW()
)

mapper.update:
UPDATE
	project_member
SET
	project_member_auth = #{projectMemberAuth}
	, active = #{active}
	, update_date = NOW()
WHERE
	work_member_no = #{workMemberNo}
AND
	project_no = #{projectNo}
