mapper.insert:


	SELECT LAST_INSERT_ID() as scheduleNo

INSERT INTO schedule (
	project_no
	, schedule_writer
	, schedule_start
	, schedule_end
	, schedule_title
	, schedule_location
	, schedule_detail_location
	, schedule_content
	, schedule_auth
	, create_date
	, update_date
) VALUES (
	#{projectNo}		
	, #{scheduleWriter} 
	, #{scheduleStart}
	, #{scheduleEnd}
	, #{scheduleTitle}
	, #{scheduleLocation}
	, #{scheduleDetailLocation}
	, #{scheduleContent}
	, #{scheduleAuth}
	, NOW()
	, NOW()
)
