mapper.insert:
INSERT INTO schedule_member (
	schedule_no
	, schedule_member_email
	, create_date
	, update_date
) VALUES (
	#{scheduleNo}
	, #{scheduleMemberEmail}
	, NOW()
	, NOW()
)

mapper.update:
UPDATE
	schedule_member
SET
	schedule_attend = #{scheduleAttend}
WHERE
	schedule_no = #{scheduleNo}
AND
	schedule_member_email = #{scheduleMemberEmail}

mapper.delete:
DELETE FROM
	schedule_member
WHERE
	schedule_no = #{scheduleNo}
AND
	schedule_member_email = #{scheduleMemberEmail}
