mapper.insert:
INSERT INTO task (
	task_title
	, task_writer
	, create_date
	, update_date
	, tasklist_no
) VALUES (
	#{taskTitle}
	, #{taskWriter}
	, NOW()
	, NOW()
	, #{tasklistNo}
)

mapper.update:
UPDATE task 
SET
	task_title 		= #{taskTitle}
	, task_content 	= #{taskContent}
	, task_point 	= #{taskPoint}
	, task_start	= if(#{taskStart}='NaN-0NaN-0NaN 0NaN:0NaN:0NaN', null, #{taskStart})  
	, task_deadline = if(#{taskDeadline}='NaN-0NaN-0NaN 0NaN:0NaN:0NaN', null, #{taskDeadline})
	, task_end 		= if(#{taskEnd}='NaN-0NaN-0NaN 0NaN:0NaN:0NaN', null, #{taskEnd})
	, update_date 	= NOW()
WHERE 
	task_no 		= #{taskNo}
