mapper.insert:
INSERT INTO task_member (
	task_no
	, project_member_no
) VALUES (
	#{taskNo}
	, #{projectMemberNo}
)

mapper.delete:
DELETE FROM task_member
WHERE
	task_no = #{taskNo} AND project_member_no = #{projectMemberNo}

		DELETE FROM task_member WHERE task_no 
 		IN (
 			SELECT task_no FROM (
 				SELECT task_no
				FROM task 
				WHERE task_upper_no 	= #{taskNo}
			) a
		)

		DELETE FROM task_member where task_no
		IN (
			SELECT task_no 
			FROM task 
			WHERE task_upper_no 
 			IN (
 				SELECT task_no FROM (
 					SELECT task_no
					FROM task 
					WHERE task_upper_no 	= #{taskNo}
				) a
			)
		)
