mapper.select:
SELECT work_member_no workMemberNo , work_member_name workMemberName , work_member_addr workMemberAddr , work_member_birth workMemberBirth , work_member_phone workMemberPhone , work_member_dept workMemberDept , work_member_pos workMemberPos , active active , work_member_level workMemberLevel , create_date createDate , update_date updateDate , work_no workNo , work_member_email workMemberEmail
FROM workspace_member
WHERE work_member_no = #{workMemberNo}

